function biblioteca_plugin_uninstall() {
    global $wpdb;
    
    // Excluir tabela
    $table_name = $wpdb->prefix . 'biblioteca';
    $wpdb->query("DROP TABLE IF EXISTS $table_name");
    
    // Limpar quaisquer opções relacionadas
    delete_option('biblioteca_db_version');
}

function biblioteca_table_creator()
{
    global $wpdb;
    $charset_collate = $wpdb->get_charset_collate();
    $table_name = $wpdb->prefix . 'biblioteca';
    $sql = "DROP TABLE IF EXISTS $table_name;
            CREATE TABLE $table_name(
            id mediumint(11) NOT NULL AUTO_INCREMENT,

            id_item varchar(10) NOT NULL,
            categoria varchar(350) NOT NULL,
            titulo TEXT NOT NULL,
            autor varchar(100) NOT NULL,
            ano varchar(5) NOT NULL,
            link TEXT NOT NULL,
            resumo TEXT NOT NULL,
            palavra_chave varchar(100) NOT NULL,

            PRIMARY KEY id(id)
            )$charset_collate;";
    require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
    dbDelta($sql);

    cwpai_insert_data_into_biblioteca_table();

}

